mapper.update:
UPDATE visit_log
SET preference = #{preference}
WHERE id = #{visitLogId} AND user_id = #{userId}

mapper.select:
SELECT EXISTS (
SELECT 1 FROM visit_log WHERE id = #{visitLogId} AND user_id = #{userId}
)

SELECT EXISTS (
SELECT 1
FROM visit_log
WHERE user_id = #{userId}
AND attraction_no = #{attractionNo}
AND DATE(entered_at) = #{date}
)

SELECT COUNT(*)
FROM visit_log
WHERE user_id = #{userId}
